import { useState } from "react";
import { creatAuthUserWithEmailAndPassword } from "../../utils/firebase/firebase.utils";
const defaultformfields = {

    displayName: '',
    email: '',
    password: '',
    confirmPassword : '',
}




const SignUpForm = () => {

    const { formfields, setformfields } = useState(defaultformfields);
    const { displayName, email, password, confirmPassword } = formfields;

    const handleSubmit = async (event) => {
        event.preventdefault();
        if (password != confirmPassword) {
            alert("passwords do not match");
            return;
        }

    }

    const handleChange = (event) => {
        const { name,value } = event.target;

        setformfields({ ...formfields, [name]: value });
}
    return (
        <div>
            <h1>sign in</h1>
            <form onSubmit={()=>{}}>
                <label >Display Name</label>
                <input type="text" required name="displayName"value={displayName} />

                <label >Email</label>
                <input type="email" required name="email" value={email}/>

                <label >Password</label>
                <input type="password" required name="password" value={password}/>

                <label >Confirm Password</label>
                <input type="password" required name="confirmaPassword" value={confirmPassword}/>

                <button type="submit"></button>


            </form>
        </div>
    );
}
export default SignUpForm;
