import { useContext } from 'react';
import { CartContext, addItemToCar} from '../../contexts/cart.context';

import './checkout.styles.scss'

const Checkout = () => {

    const { cartItems,addItemToCart} = useContext(CartContext);
    return(
        <div>
            {cartItems.map((cartItem) => {
                const { id, quantity, name } = cartItem;
                return (
                    <div key={id}>
                        <h2>
                            {name}
                        </h2>
                        <span>
                            {quantity}
                        </span>
                        <br />
                        <span>
                            decrement
                        </span>
                        <br />
                        <span onClick={()=>addItemToCart(cartItem)}>
                            increment
                        </span>
                    </div>
                )
            })}
        </div >
    )
}
export default Checkout;
